mapper.select:
	SELECT ${selectColumnNames}
	FROM ${tableName} o


	WHERE o.${columnName} = #{columnValue}



	WHERE o.${columnName} IN		

            #{item}



	ORDER BY o.id DESC

SELECT ${selectColumnNames}
FROM ${tableName} o
WHERE o.${columnName} = #{columnValue}
ORDER BY o.id DESC
LIMIT 1

SELECT ${selectColumnNames}
FROM ${tableName} o
WHERE 1=1	



   	AND ${item.columnName} = #{item.columnValue}


   	AND ${item.columnName} IS NULL



ORDER BY o.id DESC

mapper.update:
		UPDATE ${tableName}
		SET		

            ${item.columnName} = #{item.columnValue},

			updateTime = NOW()			
		WHERE id = #{id}

UPDATE ${tableName}
SET 
	deleteStatus = 1,  
	updateTime = NOW()

WHERE id = #{id}


WHERE id IN (${ids})

mapper.delete:
DELETE FROM ${tableName}

WHERE id = #{id}


WHERE id IN (${ids})
